import React from 'react';
import { FaBars } from 'react-icons/fa';
import { useGlobalContext } from './context';

const Home = () => {
  const { openSidebar, closeSidebar, openModal, closeModal } = useGlobalContext();
  return (
    <main>
      <button onClick={openSidebar, closeSidebar} className='sidebar-toggle'>
        <FaBars />
      </button>
      <button onClick={e => {openModal, closeModal}} className='btn'>
        show modal
      </button>
    </main>
  );
};

export default Home;
